<?php

$sql = "SELECT * FROM associations WHERE id=".$_GET['id'];

$associations = $pdo->query($sql)->fetchAll();
echo'<div class="container">';
    echo'<div class="row">';
        foreach ($associations as $value){
            echo'<div class="col-6">';
                echo'<img src="https://www.helloasso.com/assets/img/logos/association-coopalim-de-strasbourg-4f182bc1f10644ada0850ce91d569a09.png?bb=0x0x200x200&sb=200x200" />';
            echo'</div>';

            echo'<div class="col-6">';
                echo'<h1>'.$value['name'].'</h1>';
                echo'<p>'.$value['description'].'</p>';
                echo'<h1>'.$value['auteur'].'</h1>';
                if($value['status']==1){
                    echo'Actif <img src="data:image/png;base64," width="16px" />';
                }else{
                    echo'Non actif <img src="data:image/png;base64," width="16px" />';
                }
            echo'</div>';
        }
    echo'</div>';
echo'</div>';

?>


